INSERT INTO `spglwf_posts` VALUES("272", "1", "2016-10-28 15:32:17", "2016-10-28 15:32:17", "[et_pb_section admin_label=\"section\" transparent_background=\"off\" allow_player_pause=\"off\" inner_shadow=\"off\" parallax=\"off\" parallax_method=\"off\" padding_mobile=\"off\" make_fullwidth=\"off\" use_custom_width=\"off\" width_unit=\"on\" make_equal=\"off\" use_custom_gutter=\"off\" module_id=\"product-sustainability\"][et_pb_row admin_label=\"row\"][et_pb_column type=\"4_4\"][et_pb_text admin_label=\"Text\" background_layout=\"light\" text_orientation=\"left\" use_border_color=\"off\" border_color=\"#ffffff\" border_style=\"solid\"]\r\n<h3><strong>Sustainability</strong></h3>\r\n<h4>Over the years, Woodfit have constantly strived to stay ahead of the industry curve in terms of sustainability and we remain committed today!</h4>\r\nThe wood we use at Woodfit Acoustics is a naturally sustainable material, being sourced from well-managed, renewable forests. The method of facing fibreboard with natural wood veneers is used in the vast majority of our products and is a highly efficient way of harnessing the beauty of wood, with .6mm veneer providing up to forty times the coverage of 25mm solid.\r\n\r\nFibreboard is manufactured from wood fibres sourced from forestry bi-product and co-products, allowing for the efficient use of materials that would otherwise be wasted.\r\n\r\nWe maintain a policy of reducing the emissions and waste associated with our products on a constant basis. Wherever possible, we use VOC free finishes and low formaldehyde MDF boards.\r\n\r\n[/et_pb_text][/et_pb_column][/et_pb_row][et_pb_row admin_label=\"Row\" make_fullwidth=\"off\" use_custom_width=\"off\" width_unit=\"on\" use_custom_gutter=\"off\" padding_mobile=\"off\" background_color=\"#eff1f2\" allow_player_pause=\"off\" parallax=\"off\" parallax_method=\"off\" make_equal=\"off\" parallax_1=\"off\" parallax_method_1=\"off\" parallax_2=\"off\" parallax_method_2=\"off\" column_padding_mobile=\"on\"][et_pb_column type=\"2_3\"][et_pb_text admin_label=\"Text\" background_layout=\"light\" text_orientation=\"left\" use_border_color=\"off\" border_color=\"#ffffff\" border_style=\"solid\"]\r\n<h4>Woodfit Acoustics hold \"FSC Chain of Custody Certification,\" allowing us to provide FSC certified products, sourced from sustainably and responsibly managed forests around the world.</h4>\r\n[/et_pb_text][/et_pb_column][et_pb_column type=\"1_3\"][et_pb_image admin_label=\"Image\" src=\"http://woodfit.dev/wp-content/uploads/2016/10/FSC-logo.png\" show_in_lightbox=\"off\" url_new_window=\"off\" use_overlay=\"off\" animation=\"off\" sticky=\"on\" align=\"left\" force_fullwidth=\"off\" always_center_on_mobile=\"on\" use_border_color=\"off\" border_color=\"#ffffff\" border_style=\"solid\"] [/et_pb_image][/et_pb_column][/et_pb_row][/et_pb_section]", "", "", "inherit", "closed", "closed", "", "268-revision-v1", "", "", "2016-10-28 15:32:17", "2016-10-28 15:32:17", "", "268", "http://woodfit.dev/2016/10/28/268-revision-v1/", "0", "revision", "", "0");
